"use client";
import { CardBody, CardContainer, CardItem } from "@/components/ui/3d-card";

export function ThreeDCardDemo() {
  return (
    <CardContainer>
      <CardBody>
        <CardItem translateZ="50">
          <h3>{{title}}</h3>
        </CardItem>
        <CardItem translateZ="100">
          <img src="{{image}}" alt="{{title}}" />
        </CardItem>
      </CardBody>
    </CardContainer>
  );
}

