"use client";
import { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

export function ScrollReveal({ children, direction = "up", delay = 0, duration = 0.8, distance = 50 }) {
  const ref = useRef(null);

  useEffect(() => {
    const dirs = {
      up: { y: distance },
      down: { y: -distance },
      left: { x: distance },
      right: { x: -distance },
      none: {}
    };

    gsap.fromTo(ref.current,
      { opacity: 0, ...dirs[direction] },
      {
        opacity: 1, x: 0, y: 0,
        duration,
        delay,
        scrollTrigger: {
          trigger: ref.current,
          start: "top 85%",
          toggleActions: "play none none reverse"
        }
      }
    );
  }, [direction, delay, duration, distance]);

  return <div ref={ref}>{children}</div>;
}

"use client";
import { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

export function StaggerList({ children, stagger = 0.1, direction = "up" }) {
  const ref = useRef(null);

  useEffect(() => {
    const items = ref.current.children;
    const dirs = {
      up: { y: 30 }, down: { y: -30 },
      left: { x: 30 }, right: { x: -30 }
    };

    gsap.fromTo(items,
      { opacity: 0, ...dirs[direction] },
      {
        opacity: 1, x: 0, y: 0,
        duration: 0.6,
        stagger,
        scrollTrigger: {
          trigger: ref.current,
          start: "top 80%"
        }
      }
    );
  }, [stagger, direction]);

  return <div ref={ref}>{children}</div>;
}

"use client";
import { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

export function ParallaxSection({ backgroundImage, speed = 0.5, children, height = "100vh" }) {
  const bgRef = useRef(null);
  const containerRef = useRef(null);

  useEffect(() => {
    gsap.to(bgRef.current, {
      yPercent: -50 * speed,
      ease: "none",
      scrollTrigger: {
        trigger: containerRef.current,
        start: "top bottom",
        end: "bottom top",
        scrub: true
      }
    });
  }, [speed]);

  return (
    <div ref={containerRef} className="relative overflow-hidden" style={{ height }}>
      <div
        ref={bgRef}
        className="absolute inset-0 -top-[20%] h-[140%] bg-cover bg-center"
        style={{ backgroundImage: `url(${backgroundImage})` }}
      />
      <div className="relative z-10 h-full">{children}</div>
    </div>
  );
}

"use client";
import { useRef, useEffect } from "react";
import gsap from "gsap";

export function TextSplit({ text, type = "chars", stagger = 0.03, animation = "fade" }) {
  const ref = useRef(null);

  useEffect(() => {
    const el = ref.current;
    const items = type === "chars" ? text.split("") : text.split(" ");
    el.innerHTML = items.map(item => 
      `<span class="inline-block">${item}${type === "words" ? "&nbsp;" : ""}</span>`
    ).join("");

    const animations = {
      fade: { opacity: 0 },
      slide: { opacity: 0, y: 20 },
      scale: { opacity: 0, scale: 0 },
      rotate: { opacity: 0, rotateX: 90 }
    };

    gsap.fromTo(el.children, animations[animation], {
      opacity: 1, y: 0, scale: 1, rotateX: 0,
      duration: 0.5,
      stagger,
      ease: "power2.out"
    });
  }, [text, type, stagger, animation]);

  return <span ref={ref} />;
}

"use client";
import { useRef } from "react";
import gsap from "gsap";

export function MagneticButton({ children, strength = 0.3 }) {
  const ref = useRef(null);

  const handleMouseMove = (e) => {
    const { left, top, width, height } = ref.current.getBoundingClientRect();
    const x = (e.clientX - left - width / 2) * strength;
    const y = (e.clientY - top - height / 2) * strength;
    gsap.to(ref.current, { x, y, duration: 0.3, ease: "power2.out" });
  };

  const handleMouseLeave = () => {
    gsap.to(ref.current, { x: 0, y: 0, duration: 0.5, ease: "elastic.out(1, 0.3)" });
  };

  return (
    <button
      ref={ref}
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
      className="px-6 py-3 bg-primary text-primary-foreground rounded-lg font-semibold"
    >
      {children}
    </button>
  );
}

"use client";
import { useRef, useEffect, useState } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

export function AnimatedCounter({ end, start = 0, duration = 2, suffix = "", prefix = "" }) {
  const ref = useRef(null);
  const [value, setValue] = useState(start);

  useEffect(() => {
    const obj = { value: start };

    gsap.to(obj, {
      value: end,
      duration,
      ease: "power2.out",
      scrollTrigger: {
        trigger: ref.current,
        start: "top 80%",
        toggleActions: "play none none reverse"
      },
      onUpdate: () => setValue(Math.round(obj.value))
    });
  }, [end, start, duration]);

  return <span ref={ref}>{prefix}{value.toLocaleString()}{suffix}</span>;
}

"use client";
import { useEffect, useRef } from "react";
import gsap from "gsap";
import { ScrollSmoother } from "gsap/ScrollSmoother";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger, ScrollSmoother);

export function SmoothScroll({ children, smoothness = 1 }) {
  const wrapperRef = useRef(null);
  const contentRef = useRef(null);

  useEffect(() => {
    const smoother = ScrollSmoother.create({
      wrapper: wrapperRef.current,
      content: contentRef.current,
      smooth: smoothness,
      effects: true
    });

    return () => smoother.kill();
  }, [smoothness]);

  return (
    <div ref={wrapperRef} id="smooth-wrapper">
      <div ref={contentRef} id="smooth-content">
        {children}
      </div>
    </div>
  );
}
