export function HeroSection() {
  return (
    <section className="py-24 px-6">
      <div className="max-w-6xl mx-auto text-center">
        <h1 className="text-5xl font-bold mb-6">{{headline}}</h1>
        <p className="text-xl text-muted-foreground mb-8 max-w-2xl mx-auto">{{subheadline}}</p>
        <div className="flex gap-4 justify-center">
          <button className="px-6 py-3 bg-primary text-primary-foreground rounded-lg">{{primaryCta}}</button>
          <button className="px-6 py-3 border rounded-lg">{{secondaryCta}}</button>
        </div>
      </div>
    </section>
  );
}

import { Zap, Shield, Rocket } from "lucide-react";

export function FeaturesGrid() {
  const features = [
    { icon: Zap, title: "Lightning Fast", description: "Optimized for speed" },
    { icon: Shield, title: "Secure", description: "Enterprise-grade security" },
    { icon: Rocket, title: "Scalable", description: "Grows with your business" }
  ];

  return (
    <section className="py-24 px-6">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold text-center mb-4">{{title}}</h2>
        <p className="text-center text-muted-foreground mb-12">{{description}}</p>
        <div className="grid md:grid-cols-3 gap-8">
          {features.map((f, i) => (
            <div key={i} className="p-6 border rounded-xl">
              <f.icon className="w-10 h-10 mb-4" />
              <h3 className="font-semibold mb-2">{f.title}</h3>
              <p className="text-muted-foreground">{f.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export function Testimonials() {
  const testimonials = [
    { quote: "Amazing product!", author: "John Doe", role: "CEO", avatar: "/avatar1.jpg" },
    { quote: "Changed our workflow", author: "Jane Smith", role: "CTO", avatar: "/avatar2.jpg" }
  ];

  return (
    <section className="py-24 px-6 bg-muted/50">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-3xl font-bold text-center mb-12">{{title}}</h2>
        <div className="grid md:grid-cols-2 gap-8">
          {testimonials.map((t, i) => (
            <div key={i} className="p-8 bg-background rounded-xl border">
              <p className="text-lg mb-6">"{t.quote}"</p>
              <div className="flex items-center gap-4">
                <img src={t.avatar} className="w-12 h-12 rounded-full" />
                <div>
                  <p className="font-semibold">{t.author}</p>
                  <p className="text-sm text-muted-foreground">{t.role}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export function PricingSection() {
  const plans = [
    { name: "Starter", price: "$9", features: ["Feature 1", "Feature 2"] },
    { name: "Pro", price: "$29", features: ["All Starter", "Feature 3", "Feature 4"], popular: true },
    { name: "Enterprise", price: "Custom", features: ["All Pro", "Feature 5", "Priority support"] }
  ];

  return (
    <section className="py-24 px-6">
      <h2 className="text-3xl font-bold text-center mb-12">{{title}}</h2>
      <div className="max-w-5xl mx-auto grid md:grid-cols-3 gap-8">
        {plans.map((plan, i) => (
          <div key={i} className={`p-8 rounded-xl border ${plan.popular ? 'border-primary ring-2 ring-primary' : ''}`}>
            <h3 className="text-xl font-semibold">{plan.name}</h3>
            <p className="text-4xl font-bold my-4">{plan.price}<span className="text-lg">/mo</span></p>
            <ul className="space-y-2 mb-8">
              {plan.features.map((f, j) => <li key={j}>✓ {f}</li>)}
            </ul>
            <button className="w-full py-3 bg-primary text-primary-foreground rounded-lg">Get Started</button>
          </div>
        ))}
      </div>
    </section>
  );
}

export function CtaSection() {
  return (
    <section className="py-24 px-6 bg-primary text-primary-foreground">
      <div className="max-w-4xl mx-auto text-center">
        <h2 className="text-4xl font-bold mb-4">{{title}}</h2>
        <p className="text-xl opacity-90 mb-8">{{description}}</p>
        <button className="px-8 py-4 bg-background text-foreground rounded-lg font-semibold">{{buttonText}}</button>
      </div>
    </section>
  );
}

export function Footer() {
  const links = [
    { title: "Product", items: ["Features", "Pricing", "Changelog"] },
    { title: "Company", items: ["About", "Blog", "Careers"] },
    { title: "Legal", items: ["Privacy", "Terms"] }
  ];

  return (
    <footer className="py-12 px-6 border-t">
      <div className="max-w-6xl mx-auto">
        <div className="grid md:grid-cols-4 gap-8 mb-8">
          <div>
            <h3 className="font-bold text-xl mb-4">{{companyName}}</h3>
            <p className="text-muted-foreground">Building the future.</p>
          </div>
          {links.map((group, i) => (
            <div key={i}>
              <h4 className="font-semibold mb-4">{group.title}</h4>
              <ul className="space-y-2">
                {group.items.map((item, j) => (
                  <li key={j}><a href="#" className="text-muted-foreground hover:text-foreground">{item}</a></li>
                ))}
              </ul>
            </div>
          ))}
        </div>
        <div className="pt-8 border-t text-center text-muted-foreground">
          © 2024 {{companyName}}. All rights reserved.
        </div>
      </div>
    </footer>
  );
}

export function FaqSection() {
  const faqs = [
    { q: "How does it work?", a: "Simply sign up and start building." },
    { q: "Is there a free trial?", a: "Yes, 14 days free trial." },
    { q: "Can I cancel anytime?", a: "Yes, no questions asked." }
  ];

  return (
    <section className="py-24 px-6">
      <div className="max-w-3xl mx-auto">
        <h2 className="text-3xl font-bold text-center mb-12">{{title}}</h2>
        <div className="space-y-4">
          {faqs.map((faq, i) => (
            <details key={i} className="p-4 border rounded-lg">
              <summary className="font-semibold cursor-pointer">{faq.q}</summary>
              <p className="mt-4 text-muted-foreground">{faq.a}</p>
            </details>
          ))}
        </div>
      </div>
    </section>
  );
}
