export function GlassCard({ title, children, blur = "md", opacity = 0.1 }) {
  const blurClass = { sm: "backdrop-blur-sm", md: "backdrop-blur-md", lg: "backdrop-blur-lg", xl: "backdrop-blur-xl" };
  return (
    <div className={`${blurClass[blur]} bg-white/[${opacity}] border border-white/20 rounded-2xl p-6 shadow-xl`}>
      {title && <h3 className="text-xl font-semibold mb-4">{title}</h3>}
      {children}
    </div>
  );
}

export function GradientButton({ children, variant = "purple", size = "md" }) {
  const gradients = {
    purple: "from-purple-500 to-pink-500",
    blue: "from-blue-500 to-cyan-500",
    green: "from-green-500 to-emerald-500",
    orange: "from-orange-500 to-red-500",
    pink: "from-pink-500 to-rose-500"
  };
  const sizes = { sm: "px-4 py-2 text-sm", md: "px-6 py-3", lg: "px-8 py-4 text-lg" };
  return (
    <button className={`${sizes[size]} bg-gradient-to-r ${gradients[variant]} text-white font-semibold rounded-lg hover:opacity-90 transition-all hover:scale-105`}>
      {children}
    </button>
  );
}

export function GlowEffect({ color = "#8b5cf6", intensity = "medium", children }) {
  const shadows = {
    low: `0 0 10px ${color}40`,
    medium: `0 0 20px ${color}60, 0 0 40px ${color}40`,
    high: `0 0 20px ${color}80, 0 0 40px ${color}60, 0 0 60px ${color}40`
  };
  return (
    <div style={{ filter: `drop-shadow(${shadows[intensity]})` }}>
      {children}
    </div>
  );
}

export function AnimatedBorder({ children, borderWidth = 2, speed = "normal" }) {
  const speeds = { slow: "6s", normal: "3s", fast: "1.5s" };
  return (
    <div className="relative p-[2px] rounded-xl overflow-hidden">
      <div 
        className="absolute inset-0 bg-gradient-to-r from-purple-500 via-pink-500 to-cyan-500"
        style={{ animation: `spin ${speeds[speed]} linear infinite` }}
      />
      <div className="relative bg-background rounded-xl p-6">
        {children}
      </div>
    </div>
  );
}

export function NoiseTexture({ opacity = 0.05, children }) {
  return (
    <div className="relative">
      {children}
      <div 
        className="absolute inset-0 pointer-events-none"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)'/%3E%3C/svg%3E")`,
          opacity
        }}
      />
    </div>
  );
}

export function ShimmerButton({ children, variant = "dark" }) {
  const base = variant === "dark" ? "bg-zinc-900 text-white" : "bg-white text-zinc-900";
  return (
    <button className={`relative px-6 py-3 ${base} rounded-lg font-semibold overflow-hidden group`}>
      <span className="relative z-10">{children}</span>
      <div className="absolute inset-0 -translate-x-full group-hover:translate-x-full transition-transform duration-1000 bg-gradient-to-r from-transparent via-white/20 to-transparent" />
    </button>
  );
}
