  "shadcn-toggle": "\"use client\"\n\nimport { Toggle } from \"@/components/ui/toggle\"\nimport { Bold, Italic, Underline } from \"lucide-react\"\nimport { cn } from \"@/lib/utils/cn\"\n\nconst iconMap: Record<string, React.ComponentType<{ className?: string }>> = {\n  bold: Bold,\n  italic: Italic,\n  underline: Underline,\n}\n\nexport interface ShadcnToggleProps {\n  pressed?: boolean\n  icon?: string\n  label?: string\n  variant?: \"default\" | \"outline\"\n  size?: \"default\" | \"sm\" | \"lg\"\n  className?: string\n}\n\nexport default function ShadcnToggle({\n  pressed = false,\n  icon = \"bold\",\n  label,\n  variant = \"default\",\n  size = \"default\",\n  className,\n}: ShadcnToggleProps) {\n  const Icon = iconMap[icon]\n\n  return (\n    <Toggle\n      variant={variant}\n      size={size}\n      defaultPressed={pressed}\n      aria-label={label || `Toggle ${icon}`}\n      className={cn(className)}\n    >\n      {Icon && <Icon className=\"h-4 w-4\" />}\n      {label && <span className={cn(Icon && \"ml-2\")}>{label}</span>}\n    </Toggle>\n  )\n}\n",
  "shadcn-toggle-group": "\"use client\"\n\nimport { ToggleGroup, ToggleGroupItem } from \"@/components/ui/toggle-group\"\nimport { Bold, Italic, Underline, AlignLeft, AlignCenter, AlignRight } from \"lucide-react\"\nimport { cn } from \"@/lib/utils/cn\"\n\nconst iconMap: Record<string, React.ComponentType<{ className?: string }>> = {\n  bold: Bold,\n  italic: Italic,\n  underline: Underline,\n  \"align-left\": AlignLeft,\n  \"align-center\": AlignCenter,\n  \"align-right\": AlignRight,\n}\n\nexport interface ToggleGroupItemData {\n  value: string\n  label?: string\n  icon?: string\n}\n\nexport interface ShadcnToggleGroupProps {\n  items?: ToggleGroupItemData[]\n  type?: \"single\" | \"multiple\"\n  variant?: \"default\" | \"outline\"\n  className?: string\n}\n\nexport default function ShadcnToggleGroup({\n  items = [\n    { value: \"bold\", icon: \"bold\", label: \"Bold\" },\n    { value: \"italic\", icon: \"italic\", label: \"Italic\" },\n    { value: \"underline\", icon: \"underline\", label: \"Underline\" },\n  ],\n  type = \"multiple\",\n  variant = \"default\",\n  className,\n}: ShadcnToggleGroupProps) {\n  return (\n    <ToggleGroup type={type} variant={variant} className={cn(className)}>\n      {items.map((item) => {\n        const Icon = item.icon ? iconMap[item.icon] : null\n\n        return (\n          <ToggleGroupItem\n            key={item.value}\n            value={item.value}\n            aria-label={item.label || item.value}\n          >\n            {Icon && <Icon className=\"h-4 w-4\" />}\n            {!Icon && item.label && <span>{item.label}</span>}\n          </ToggleGroupItem>\n        )\n      })}\n    </ToggleGroup>\n  )\n}\n",
  "shadcn-alert": "\"use client\"\n\nimport { Alert, AlertDescription, AlertTitle } from \"@/components/ui/alert\"\nimport { AlertCircle, CheckCircle2, Info, AlertTriangle } from \"lucide-react\"\n\nconst iconMap = {\n  default: Info,\n  destructive: AlertCircle,\n  success: CheckCircle2,\n  warning: AlertTriangle,\n}\n\nexport interface ShadcnAlertProps {\n  title?: string\n  description?: string\n  variant?: \"default\" | \"destructive\" | \"success\" | \"warning\"\n  showIcon?: boolean\n  className?: string\n}\n\nexport default function ShadcnAlert({\n  title = \"Heads up!\",\n  description = \"You can add components to your app using the CLI.\",\n  variant = \"default\",\n  showIcon = true,\n  className,\n}: ShadcnAlertProps) {\n  const Icon = iconMap[variant] || Info\n\n  return (\n    <Alert\n      variant={variant === \"success\" || variant === \"warning\" ? \"default\" : variant}\n      className={className}\n    >\n      {showIcon && <Icon className=\"h-4 w-4\" />}\n      {title && <AlertTitle>{title}</AlertTitle>}\n      {description && <AlertDescription>{description}</AlertDescription>}\n    </Alert>\n  )\n}\n",
